	public static ArrayList<Integer> bubbleSort(ArrayList<Integer> arr) {
		ArrayList<Integer> tmp = arr;
		boolean unsorted = true;
		while(unsorted) {
			boolean swapped = false;
			for(int i = 0; i < tmp.size() - 1; ++i) {
				int now  = tmp.get(i);
				int next = tmp.get(i+1);
				if(now > next) {
					tmp.set(i+1, now);
					tmp.set(i, next);
					swapped = true;
				}
			}	
			unsorted = swapped;
		}
		return tmp;
	}
